import axios from 'axios'
import React, { useState, useEffect } from 'react';
import './App.css';
import SearchField from './SearchField/SearchField';


const App = () => {
  const [data, setData] = useState();


  const handleSubmit = async(keyword) => {
    try {
      const formattedKeyword = keyword.toLowerCase();
      const result = await axios.get(`https://pokeapi.co/api/v2/pokemon/${formattedKeyword}`)
      setData(result?.data)
    } catch (e) {
      console.error(e)
    }
  }
  // console.log({data})

  return (
        <div className="App">
          {/* <header className="App-header">          
          </header> */}
          <SearchField onSubmit={handleSubmit}/> 
          {/* <form onSubmit={(e) => handleSubmit(e)}>
          <input value={keyword} onChange={(e) => setKeyword(e.target.value)} />
          <button>Search</button>
          </form> */}
          <table>
          <tr>
            <th>Name:</th>
            <td>{data?.name}</td>
          </tr>
          { data?.types?.length > 0 ? (
            <tr>
              <th>Type:</th>
              {data.types.map(type => <td>{type.type.name}</td>)}
            </tr>
          ) : null}
          { data?.weight ? (
            <tr>
              <th>Weight:</th>
              <td>{data.weight}</td>
            </tr> 
          ) : null}
          { data?.base_experience ? (
            <tr>
            <th>Base Experience:</th>
            <td>{data.base_experience}</td>
            {/* <td>555 77 855</td> */}
          </tr>
          ) : null} 
          {data?.moves?.length > 0 ? (
            <tr>
              <th>Moves:</th>
              <td>
                <table>
                  {data.moves.map((move) => {
                    return <tr>{move.move.name}</tr>
                  })}
                </table>
              </td>
            </tr>
          ) : null }

          </table>
        </div>
      );
}

export default App;
