import axios from 'axios'
import React, { useState, useEffect } from 'react';
import './App.scss';
import ResultTable from './ResultTable/ResultTable';
import SearchField from './SearchField/SearchField';


const App = () => {
  const [isInitialState, setIsInitialState] = useState(true)
  const [data, setData] = useState();

  const handleSubmit = async(e, keyword) => {
    e.preventDefault();
    try {
      const formattedKeyword = keyword.toLowerCase();
      const result = await axios.get(`https://pokeapi.co/api/v2/pokemon/${formattedKeyword}`)
      setData(result?.data)
    } catch (e) {
      console.error(e)
    }
  }

  return (
        <div className="App">
          <header>
            <img src={''}/>
            <h1>  
              Pokemon Search 
            </h1>       
          </header>
          <SearchField onSubmit={handleSubmit}/>
          {isInitialState ? <div>Type to make a search.</div> : null}
          {isInitialState && data ?  <ResultTable data={data}/> : <div>No Results</div>}
        </div>
      );
}

export default App;
